import LineChart from "../LineChart.js";
import { transactions } from "../firebase";

export default {
    name: "line",
    components: {
        LineChart
    },
    data() {
        return {
            user: null,
            chartOptions: {
                scales: {
                    xAxes: [{
                        type: 'time',
                        distribution: 'linear',
                        time: {
                            unit: 'day',
                            min: this.getFirstDay(),
                            max: this.getLastDay()
                        }
                    }]
                },
                maintainAspectRatio:false
            }
        };
    },
    methods: {
        getFirstDay() {
            var date = new Date();
            var firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
            return firstDay;
        },
        getLastDay() {
            var date = new Date();
            var lastDay = new Date(date.getFullYear(), date.getMonth() + 1, 0);
            return lastDay;
        },
        reverseOrder(l) {
            var reversed = [];
            for(var i = l.length - 1; i >= 0; i--) {
                reversed.push(l[i]);
            }
            return reversed;
        }
    },
    firebase: {
        list: transactions.orderByChild("date")
    },
    mounted() {
        this.user = this.$store.username;
    },
    computed: {
        datacollection: function() {
            if (!this.user) {
                return null;
            } else {
                return {
                    // labels: this.getDaysOfMonth,
                    datasets: [
                        {
                            label: "Earnings",
                            data: this.earnings,
                            fill: false,
                            borderColor: "#53f442"
                            // backgroundColor: "#53f442"
                        },
                        {
                            label: "Spendings",
                            data: this.spendings,
                            fill: false,
                            borderColor: "#e82929"
                            // backgroundColor: "#e82929"
                        }
                        ,
                        {
                            label: "Balance",
                            data: this.balance,
                            fill: false,
                            borderColor: "#000000"
                            // backgroundColor: "#e82929"
                        }
                    ]
                };
            }
        },
        earnings: function() {
            var firstDay = this.getFirstDay();
            var lastDay = this.getLastDay();
            var sorted = this.list;
            var earnings = [];
            for(var i = 0; i < sorted.length; i++) {
                if(sorted[i].username === this.user
                    && new Date(sorted[i].date.toString()) >= firstDay && new Date(sorted[i].date.toString()) <= lastDay
                    && sorted[i].amount > 0) {
                    earnings.push({x: new Date(sorted[i].date), y: sorted[i].amount});
                }
            }
            return earnings;
        },
        spendings: function() {
            var firstDay = this.getFirstDay();
            var lastDay = this.getLastDay();
            var sorted = this.list;
            var spendings = [];
            for(var i = 0; i < sorted.length; i++) {
                if(sorted[i].username === this.user
                    && new Date(sorted[i].date) >= firstDay && new Date(sorted[i].date) <= lastDay
                    && sorted[i].amount < 0) {
                    spendings.push({x: new Date(sorted[i].date), y: Math.abs(sorted[i].amount)});
                }
            }
            return spendings;
        },
        balance: function() {
            var firstDay = this.getFirstDay();
            var lastDay = this.getLastDay();
            var sorted = this.list;
            var balance = [];
            var sum = 0;
            for(var i = 0; i < sorted.length; i++) {
                if(sorted[i].username === this.user
                    && new Date(sorted[i].date) >= firstDay && new Date(sorted[i].date) <= lastDay) {
                    sum += parseFloat(sorted[i].amount);
                    balance.push({x: new Date(sorted[i].date), y: sum});
                }
            }
            return balance;
        }
    }
};
